(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),s=document.querySelector(".timer-action"),m=t=>{let m=(()=>{let t=(new Date("March 15, 2022 23:28:00").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();m.timeRemaining<=0?(e.textContent="00",n.textContent="00",o.textContent="00",s.textContent="На данный момент акция закончилась...",clearInterval(t)):(e.textContent=m.hours<10?"0"+m.hours:m.hours,n.textContent=m.minutes<10?"0"+m.minutes:m.minutes,o.textContent=m.seconds<10?"0"+m.seconds:m.seconds)};(()=>{let t;t=setInterval(m,1e3,t)})()})()})();